{
    "openapi": "3.1.0",
    "info": {
        "title": "Greeting Services",
        "description": "Demonstrate declarative services and basic extensions points",
        "contact": {
            "email": "aftersound@gmail.com"
        },
        "version": "1.0.0"
    },
    "paths": {
        "/greet/{name}": {
            "get": {
                "summary": "greet a person or entity by name",
                "description": "Greet service without auth and rate limit",
                "operationId": "greet",
                "parameters": [
                    {
                        "name": "name",
                        "in": "path",
                        "description": "the name of person or entity to greet",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "greeting happens",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Greeting"
                                }
                            }
                        }
                    },
                    "500": {
                        "description": "internal error",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Messages"
                                }
                            }
                        }
                    }
                },
                "extensions": {
                    "x-weave-execution-control": {
                        "type": "GreetingService",
                        "greetingWords": [
                            "Hello",
                            "Hola",
                            "您好"
                        ]
                    }
                }
            }
        },
        "/greet1/{name}": {
            "get": {
                "summary": "greet a person or entity by name",
                "description": "Greet service with auth but without rate limit",
                "operationId": "greet",
                "parameters": [
                    {
                        "name": "name",
                        "in": "path",
                        "description": "the name of person or entity to greet",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "greeting happens",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Greeting"
                                }
                            }
                        }
                    },
                    "500": {
                        "description": "internal error",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Messages"
                                }
                            }
                        }
                    }
                },
                "extensions": {
                    "x-weave-auth-control": {
                        "type": "Demo",
                        "userCredentials": {
                            "user": "W6ph5Mm5Pz8GgiULbPgzG37mj9g="
                        }
                    },
                    "x-weave-execution-control": {
                        "type": "GreetingService",
                        "greetingWords": [
                            "Hello",
                            "Hola",
                            "您好"
                        ]
                    }
                }
            }
        },
        "/greet2/{name}": {
            "get": {
                "summary": "greet a person or entity by name",
                "description": "Greet service without auth but with rate limit",
                "operationId": "greet",
                "parameters": [
                    {
                        "name": "name",
                        "in": "path",
                        "description": "the name of person or entity to greet",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "greeting happens",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Greeting"
                                }
                            }
                        }
                    },
                    "500": {
                        "description": "internal error",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/Messages"
                                }
                            }
                        }
                    }
                },
